use criterion::{black_box, criterion_group, criterion_main, Criterion};
use gskits::dna::reverse_complement;

static DNA_SEQ: &str = "";

fn reverse_complement_benchmark(c: &mut Criterion) {
    c.bench_function("reverse_complement", |b| {
        b.iter(|| reverse_complement(black_box(DNA_SEQ.as_bytes())))
    });
}

criterion_group!(benches, reverse_complement_benchmark,);
criterion_main!(benches);
